import React from 'react';
import ReactDOM from 'react-dom';

class MyForm extends React.Component {
    change(check){
        let val = check.target.value;
        if(Number(val)){
           document.getElementById('error').innerHTML = "Please Enter a valid text";
        }else if(val==""){
            document.getElementById('error').innerHTML = "You can't enter an empty space";
        }
    }

      render() {
                return(
                    <>
                        <p>Please enter your name</p>
                        <h1 id='error'></h1>
                        <h1 id='yes' className=' text-green-400'></h1>
                        <input type="text" onChange={this.change} className='border-2 border-black rounded-md mt-2' />
                    </>
            );
        }
    }
export default MyForm;
