// setInterval(Time, 1000);

const Time = () => {
    const [date, setDate] = useState(new Date());

    useEffect(() => {
        var timer = setInterval(() => { setDate(date) }, 1000);
        return(
            function cleanup() {
                clearInterval(timer);
            }
        );
    }
    );

    return(
        <>
            <h1>{date.toLocaleTimeString()}</h1>
        </>
    )
}
 
export default Time;
