import React, { useState } from "react";
import WhetherTitle from "./WhetherTitle";
import Footer from "./Footer";

const api = {
    key:'54528cc3dd8c5a966058a5bafa6bc5b5',
    base:'https://api.openweathermap.org/data/2.5/'
}

const date = (d) =>{
    let months = ['January','February','March','April','May','June','July','August','September','October','November','December'];
    let days = ['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday'];

    let day = days[d.getDay()];

    let month = months[d.getMonth()];

    let year = d.getFullYear();

    return `${day} ${month} ${year}`
}

const WhetherApp = () => {

    return(
        <div>
            <WhetherTitle value="My Whether App" />
            <div className="justify-center items-center text-center grid mt-4 mb-4">
            <div className="w-96 bg-slate-300 p-4 rounded-md bg-[url(img/blue.jpg)]">
                <input type="text" className="w-72 h-10 rounded-md drop-shadow-md p-4 text-lg" placeholder="Enter City, Year ...." />
                <p className="justify-center items-center text-center grid">
                    <p className="justify-center items-center grid text-center p-9 text-8xl mt-5 font-semibold bg-slate-600 text-white opacity-60 w-64 h-48 rounded-lg">
                        15&#176;C
                    </p>
                </p>
                <p className=" justify-start items-start text-left pl-12 pt-2 text-3xl font-semibold drop-shadow-md shadow-blue-600">
                    Nigeria, Ng 
                </p>

                <p className=" justify-start items-start text-left pl-12 text-lg font-semibold drop-shadow-md shadow-blue-600 text-blue-700">
                    {date(new Date())}
                </p>
                
            </div>
            </div>
            <Footer value="Designed by OYEKUNLE DARE EMMANUEL" />
        </div>
    )
}

export default WhetherApp;
