import React , { useState} from "react";
import CalculatorTitle from "./CalculatorTitle";
import Output from "./OutputScreenRow";
import But from "./Button";

function handleClick (event) {
    const value = event.target.value;
    switch (value) {
        case "=": {
            // if it's an equal sign, use the eval module
            // to evaluate the question ,convert the answer
            // (in number) to String
            if (this.state.question !== "") {
                var ans = "";
                try {
                    ans = eval(this.state.question);
                } catch (err) {
                    this.setState({ answer: "Math Error" });
                }
                if (ans === undefined) this.setState({ answer: "Math Error" });
                // update answer in our state.
                else this.setState({ answer: ans, question: "" });
                break;
            }
        }
        case "Clear": {
            // if it's the Clears sign, just clean our
            // question and answer in the state
            this.setState({ question: "", answer: "" });
            break;
        }
 
        case "Delete": {
            var str = this.state.question;
            str = str.substr(0, str.length - 1);
            this.setState({ question: str });
            break;
        }
 
        default: {
            // for every other command, update the answer in the state
            this.setState({ question: "Yes" , answer : "Yes" });
            break;
        }
    }
} 

class Calculate extends React.Component {
    constructor() {
        super();
        
        this.state = {
          question: "",
          answer: ""
        }
    }

    render(){
        return(
            <div className=' container justify-center items-center grid p-4 bg-gray-400'>
                <CalculatorTitle value='OYEKUNLE DARE CALCULATOR' />
                <Output />
                <div className="flex flex-row w-full space-x-4">
                    <But value="Clear" handleClick={this.handleClick}/>
                    <But value="+" handleClick = {this.handleClick}/>
                    <But value="." handleClick = {this.handleClick}/>
                    <But value="/" handleClick = {this.handleClick}/>
                </div>
                <div className="flex flex-row w-full space-x-4">
                    <But value="7" handleClick = {this.handleClick}/>
                    <But value="8" handleClick = {this.handleClick}/>
                    <But value="9" handleClick = {this.handleClick}/>
                    <But value="*" handleClick = {this.handleClick}/>
                </div>
                <div className="flex flex-row w-full space-x-4">
                    <But value="6" handleClick = {this.handleClick}/>
                    <But value="5" handleClick = {this.handleClick}/>
                    <But value="4" handleClick = {this.handleClick}/>
                    <But value="-" handleClick = {this.handleClick}/>
                </div>
                <div className="flex flex-row w-full space-x-4">
                    <But value="3" handleClick = {this.handleClick}/>
                    <But value="2" handleClick = {this.handleClick}/>
                    <But value="1" handleClick = {this.handleClick}/>
                    <But value="+" handleClick = {this.handleClick}/>
                </div>
                <div className="flex flex-row w-4/4 items-center justify-center space-x-4">
                    <But value="0" handleClick = {this.handleClick}/>
                    <But value="=" handleClick = {this.handleClick}/>
                </div>
            </div>
        );
    }
}

export default Calculate;
