import React, { useState } from "react";
import WhetherTitle from "./WhetherTitle";
import Footer from "./Footer";

const api = {
    key:'54528cc3dd8c5a966058a5bafa6bc5b5',
    base:'https://api.openweathermap.org/data/2.5/'
}

const WhetherApp = () => {

    return(
        <div>
            <WhetherTitle value="My Whether App" />
            <div className="justify-center items-center text-center grid mt-4 mb-4">
            <div className="w-96 bg-slate-300 p-4 rounded-md">
                <input type="text" className="w-72 h-10 rounded-sm  shadow-md shadow-black" />

                <p className=" justify-start items-start text-center p-9 text-7xl mt-5 font-semibold bg-slate-500 opacity-50">
                    15&#176;C
                </p>

                <p className=" justify-start items-start text-left pl-9 pt-5 text-3xl font-semibold">
                    Nigeria, Ng 
                </p>
            </div>
            </div>
            <Footer value="Designed by OYEKUNLE DARE EMMANUEL" />
        </div>
    )
}

export default WhetherApp;
