import React, { useState } from "react";
import Filter from "./Filter";
import Footer from "./Footer";
import JSonData from "../src/MOCK_DATA.json";

const Search = () => {

    const [searchTerm,setSearchTerm] = useState("");

    return(
        <div className=" justify-center items-center">
            <Filter value="Search Filter" />

            <div className="grid justify-center items-center">
            <input type="text" className=" pl-3 mb-5 h-12 w-96 bg-gray-500 bg-opacity-100 border-gray-800 text-2xl rounded-md border-2 mt-5 text-white" placeholder="Search....."
            onChange={(event) => {setSearchTerm(event.target.value)}} />
            </div>

            <div className="grid justify-center items-center">
                <table className=" table-auto font-bold border-separate">
                    <tr className=""><td className="border">#</td><td className="border">First Name</td><td className="border">Last Name</td><td className="border">Emaill Address</td></tr>
                {JSonData.filter(
                    (val) => {
                        if (searchTerm == "") {
                            return val
                        } else if(val.first_name.toLowerCase().includes(searchTerm.toLowerCase()) || val.last_name.toLowerCase().includes(searchTerm.toLowerCase()) || val.email.toLowerCase().includes(searchTerm.toLowerCase())){
                            return val
                        }else if(val == ""){
                            return(
                            <div>
                                No search returned
                            </div>
                            )
                        }
                    }
                ).map((val,key) => {
                    return(
                        <tr className="text-lg font-medium" key={key}>
                            <td>{val.id}</td><td>{val.first_name}</td> <td>{val.last_name}</td> <td>{val.email}</td>
                        </tr>
                    );
                })}
                </table>
            </div>
            
            <Footer value="Designed by OYEKUNLE DARE EMMANUEL" />
        </div>
    );
}

export default Search;
